import React, { useState } from 'react';
import { Grid, Button, TextField, Select, ButtonBase, Typography } from "@material-ui/core";
import MovieInformation from "../../Dialogs/MovieInformation"
import movie1 from "../../../Assets/1.jpg"
import movie2 from "../../../Assets/2.jpg"
import movie3 from "../../../Assets/3.jpg"
import movie4 from "../../../Assets/4.jpg"
import movie5 from "../../../Assets/5.jpg"
import movie6 from "../../../Assets/6.jpg"

export default function FilmCard(props) {

  function getImage() {

    switch (props.movie.title) {
      case "A New Hope":
        return movie1;
      case "The Empire Strikes Back":
        return movie2;
      case "Return of the Jedi":
        return movie3;
      case "The Phantom Menace":
        return movie4;
      case "Attack of the Clones":
        return movie5;
      case "Revenge of the Sith":
        return movie6
    }
  }


  const [openMovieInformation, setOpenMovieInformation] = useState(false);

  return (
    <>
      <Grid container direction="column" justify="space-around" alignItems="center" spacing={2}>
        <Grid item xs={12}>
          <ButtonBase onClick={() => setOpenMovieInformation(true)}>
            <img src={getImage()} style={{ width: "70%" }} >
            </img>
          </ButtonBase>
        </Grid>
        <Grid item xs={12}>

          <span style={{ color: "white", textJustify: "center", marginTop: 8 }}> {props.movie.title}</span>
        </Grid>
        <Grid item xs={12}>
          <MovieInformation
            open={openMovieInformation}
            onClose={() => setOpenMovieInformation(false)}
            movie={props.movie}
            allPeopleObjetic={props.allPeopleObjetic}
          ></MovieInformation>
        </Grid>
      </Grid>
    </>
  );
}
