import React, { useState, useEffect } from "react";
import * as starWarsAPI from "../../util/API/starWarsAPI";
import { Grid, Button, TextField, Select, MenuItem } from "@material-ui/core";
import Logo from "../../Assets/logo.png"
import FilmCard from "../../components/Cards/Film"

export default function Start(props) {
  const [textSearch, setTextSearch] = useState("");
  const [search, setSearch] = useState("Movie");
  const [allMoviesObjetic, setAllMoviesObject] = useState({});
  const [allMovies, setAllMovies] = useState({});
  const [allPeople, setAllPeople] = useState([]);
  const [filtredMovies, setFiltredMovies] = useState([]);

  useEffect(() => {
    async function getData() {
      await getAllFilmds();
      await getAllPeople();
    }
    getData();
  }, [])

  async function getAllFilmds() {
    const response = await starWarsAPI.getAllFilms()
    setAllMovies(response.data.results)
    let movies = {}
    response.data.results.map(movie => (
      movies[movie.url] = movie
    ))
    setAllMoviesObject(movies)
  }

  async function getAllPeople() {
    const response = await starWarsAPI.getAllPeople()
    setAllPeople(response.data.results)
  }

  async function filter(value) {
    setTextSearch(value);
    if ("pessoa" === "as") {
      setFiltredMovies(allMovies.filter((movie) =>
        movie.title.split(' ').join('').toLowerCase().includes(textSearch.split(' ').join('').toLowerCase())
      ))
    }
    else {
      let nameFilter = allPeople.filter((character) =>
        character.name.split(' ').join('').toLowerCase().includes(textSearch.split(' ').join('').toLowerCase())
      )
      console.log("nameFilter", nameFilter)
      if (nameFilter.length === 1) {
        let filtredMovies = [];

        nameFilter[0].films.map(filmUrl => {
          filtredMovies.push(allMoviesObjetic[filmUrl]);
        })
        setFiltredMovies(filtredMovies)
      }
    }
  }

  return (
    <Grid container direction="column"
      justify="center" alignItems="center">
      <img
        src={Logo}>
      </img>
      <Grid item xs={6}>
        <Grid container justify="space-between" alignItems="center">
          <Grid item xs={8}>
            <TextField
              style={{ marginBottom: 16 }}
              variant="filled"
              id="outlined-required"
              label="Pesquisa"
              variant="outlined"
              margin="dense"
              fullWidth
              value={textSearch}
              onChange={(event) => filter(event.target.value)}
            />
            {filtredMovies.map((movie) => (
              <FilmCard movie={movie} ></FilmCard>
            ))}
          </Grid>
          <Grid item xs={4}>
            <Select
              labelId="demo-simple-select-label"
              id="demo-simple-select"
              value={search}
              onChange={setSearch}
            >
              <MenuItem value={"Movie"}>Movie</MenuItem>
              <MenuItem value={"People"}>People</MenuItem>
            </Select>
          </Grid>
        </Grid>
      </Grid>
    </Grid>
  );
}
