            <span> fdsadsfdsfdsafds fds fdsa fdsa </span>
          </Grid>
        </Grid>
      </ButtonBase>
      <MovieInformation
        open={openMovieInformation}
        onClose={() => setOpenMovieInformation(false)}
        movie={props.movie}
      ></MovieInformation>
    </>
  );
}
