import React, { useState, useEffect } from "react";
import MovieInformation from "../../Dialogs/MovieInformation"
import yoda from "../../../Assets/a.webp"
import {
  Card,
  CardContent,
  CardActionArea,
  CardActions,
  Typography,
  IconButton,
  Tooltip,
  Chip,
  Button,
  CardMedia,
  Grid,
  ButtonBase
} from "@material-ui/core";

export default function Start(props) {
  const [openMovieInformation, setOpenMovieInformation] = useState(false);
  return (
    <>
      <ButtonBase onClick={() => setOpenMovieInformation(true)} >
        <Card >
          <Grid container>
            <Grid item xs={6}>
              <img
                src={yoda}
                size={20}
              >
              </img>
            </Grid>
            <Grid item xs={6}>
              <span>
                {props.movie.title}
              </span>
            </Grid>
          </Grid>
        </Card>
      </ButtonBase>

      <MovieInformation
        open={openMovieInformation}
        onClose={() => setOpenMovieInformation(false)}
        movie={props.movie}
      ></MovieInformation>
    </>
  );
}
