  })

  async function filter(value) {
    setTextSearch(value);
    const response = await starWarsAPI.getAllFilms()
    console.log("Response: ", response);
  }

  return (
    <div>
      <TextField
        style={{ marginBottom: 16 }}
        variant="filled"
        id="outlined-required"
        label="Pesquisa"
        variant="outlined"
        margin="dense"
        fullWidth
        value={textSearch}
        onChange={(event) => filter(event.target.value)}
      /></div>
  );
}
