import React, { useState, useEffect } from "react";
import * as starWarsAPI from "../../util/API/starWarsAPI";
import { Button, TextField } from "@material-ui/core";

export default function Start(props) {
  const [textSearch, setTextSearch] = useState("");
  const [allMovies, setAllMovies] = useState([]);
  const [allPeople, setAllPeople] = useState([]);

  useEffect(async () => {
    console.log("ENtrou")
    const allMovies = await starWarsAPI.getAllFilms()
    const allPeople = await starWarsAPI.getAllPeople()
    setAllMovies(allMovies.data.results)
    setAllPeople(allPeople.data.results)
  }, [])

  console.log(allMovies)
  console.log(allPeople)

  async function filter(value) {
    setTextSearch(value);
  }

  return (
    <div>
      <TextField
        style={{ marginBottom: 16 }}
        variant="filled"
        id="outlined-required"
        label="Pesquisa"
        variant="outlined"
        margin="dense"
        fullWidth
        value={textSearch}
        onChange={(event) => filter(event.target.value)}
      /></div>
  );
}
