import React, { useState } from 'react';
import { makeStyles, useTheme } from '@material-ui/core/styles';
import ButtonBase from '@material-ui/core/ButtonBase';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import SkipPreviousIcon from '@material-ui/icons/SkipPrevious';
import PlayArrowIcon from '@material-ui/icons/PlayArrow';
import SkipNextIcon from '@material-ui/icons/SkipNext';
import MovieInformation from "../../Dialogs/MovieInformation"
import yoda from "../../../Assets/a.jpg"

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
  },
  details: {
    display: 'flex',
    flexDirection: 'column',
  },
  content: {
    flex: '1 0 auto',
  },
  cover: {
    width: 151,
  },
  controls: {
    display: 'flex',
    alignItems: 'center',
    paddingLeft: theme.spacing(1),
    paddingBottom: theme.spacing(1),
  },
  playIcon: {
    height: 38,
    width: 38,
  },
}));

export default function FilmCard(props) {

  const [openMovieInformation, setOpenMovieInformation] = useState(false);
  const classes = useStyles();
  const theme = useTheme();

  return (
    <>
      <ButtonBase onClick={() => setOpenMovieInformation(true)} >
        <Card className={classes.root}>
            <CardMedia
              className={classes.cover}
              image={yoda}
            />
          <CardContent className={classes.content}>
            <Typography component="h5" variant="h5">
              Live From Space
            </Typography>
          </CardContent>
        </Card>
      </ButtonBase>
      <MovieInformation
        open={openMovieInformation}
        onClose={() => setOpenMovieInformation(false)}
        movie={props.movie}
      ></MovieInformation>
    </>
  );
}
